    private boolean CadastrarCliente(Pessoa novo) {
        boolean sucesso = false;
        
        if (!pwdSenha.getText().equals(pwdRepita.getText())){
            JOptionPane.showMessageDialog(null, "As senhas n√£o coincidem!");
            return false;
        }
        
        novo.setNome(txtNome.getText());
        novo.setSobrenome(txtSobrenome.getText());
        novo.setRg(txtRG.getText());
        novo.setEmail(txtEmail.getText());
        novo.setSexoId(cbxCadastroSexo.getSelectedIndex());
        novo.setTelefone(txtTelefone.getText());
        novo.setCpf(txtCpf.getText());
        novo.setEstadoId(cbxEstado.getSelectedIndex());
        novo.setBairro(txtBairro.getText());
        novo.setCidade(txtCidade.getText());
        novo.setRua(txtRua.getText());
        novo.setCep(txtCEP.getText());
        novo.setNumero(txtNumero.getText());
        novo.setSenha(pwdSenha.getText());
        novo.setAgiota(chbxAgiota.isSelected());
        
        if(verificarCpfJaCadastrado())
            return false;
        
        this.conectar.conectaBanco();

        try{
            String query = "INSERT INTO pessoa ("
                    +"nome,"
                    +"sobrenome,"
                    +"rg,"
                    +"email,"
                    +"sexo_id,"
                    +"telefone,"
                    +"cpf,"
                    +"estado_id,"
                    +"bairro,"
                    +"cidade,"
                    +"rua,"
                    +"cep,"
                    +"numero,"
                    +"senha,"
                    +"agiota) values ("
                    + "'" + novo.getNome() + "',"
                    + "'" + novo.getSobrenome() + "',"
                    + "'" + novo.getRg() + "',"
                    + "'" + novo.getEmail() + "',"
                    + "'" + novo.getSexoId() + "',"
                    + "'" + novo.getTelefone() + "',"
                    + "'" + novo.getCpf() + "',"
                    + "'" + novo.getEstadoId() + "',"
                    + "'" + novo.getBairro() + "',"
                    + "'" + novo.getCidade() + "',"
                    + "'" + novo.getRua() + "',"
                    + "'" + novo.getCep() + "',"
                    + "'" + novo.getNumero() + "',"
                    + "'" + novo.getSenha() + "',"
                    + novo.getAgiota() + ");";
            
            this.conectar.insertSQL(query);
            sucesso = true;
            JOptionPane.showMessageDialog(null, "Cadastro Realizado com Sucesso!!");
        } catch (Exception e ){
            sucesso = false;
            System.out.println("Erro ao Cadastrar  " + e.getMessage());
            JOptionPane.showMessageDialog(null, "Erro ao Cadastrar!");
        } finally {
            this.conectar.fechaBanco();
        }
        return sucesso;
    }
    
    private boolean verificarCpfJaCadastrado(){
        this.conectar.conectaBanco();
        String cpfExistente = "";
        
        try{
            String buscarCliente = "SELECT cpf FROM pessoa WHERE cpf = '" + this.txtCpf.getText() + "';";
            this.conectar.executarSQL (buscarCliente);
            
            
            while (this.conectar.getResultSet().next()) {
                cpfExistente = this.conectar.getResultSet().getString(1);
            }
        } catch (Exception e) {
            System.out.println("Erro ao consultar CPF j√° cadastrado: " + e.getMessage());
            JOptionPane.showMessageDialog(null, "Erro ao consultar CPF j√° cadastrado, n√£o foi poss√≠vel realizar o cadastro!");
            return true;
        } finally {
            this.conectar.fechaBanco();
        }
        
        if (!"".equals(cpfExistente)){
            JOptionPane.showMessageDialog(null, "J√° existe um cadastro para esse CPF!");
            return true;
        }
        
        return false;
    }
    
